// DOM Ready
$(function() {

	// SVG fallback
	// toddmotto.com/mastering-svg-use-for-a-retina-web-fallbacks-with-png-script#update
	if (!Modernizr.svg) {
		var imgs = document.getElementsByTagName('img');
		var dotSVG = /.*\.svg$/;
		for (var i = 0; i != imgs.length; ++i) {
			if(imgs[i].src.match(dotSVG)) {
				imgs[i].src = imgs[i].src.slice(0, -3) + "png";
			}
		}
	}

	// Target your .container, .wrapper, .post, etc.
    $(".fitvid").fitVids();

});

// High Contrast Mode
var highContrast = false;
$("#highcontrast a").click(function () {
	if (!(highContrast)) {
		$('body').append('<link rel="stylesheet" href="/specialevents/wp-content/themes/special-events/assets/css/highcontrast.min.css" type="text/css" id="hc_stylesheet"/>');
		$('#highcontrast a').text($("#highcontrast a").attr('offtext'));
		highContrast = true;
	}
	else {
		// remove the high-contrast style
		$("#hc_stylesheet").remove();
		$('#highcontrast a').text($("#highcontrast a").attr('ontext'));
		highContrast = false;
	}
});


// Call to iSotope Masonry

var $container = $('#specialevents').isotope({
  // main isotope options
  itemSelector: '.post',
  // filter: '.show-all',
  masonry: {
    columnWidth: 220,
    gutter: 20
  },
  getSortData : {
    selected : function( $item ){
      // sort by selected first, then by original order
      return ($item.hasClass('event-date') ? -1000 : 0 ) + $item.index();
    }
  },
  sortBy : 'event-date'
});

$('#filter').find('a').click(function() {
      // get the href attribute
      var filterName = '.' + $(this).attr('href').slice(1);
      filterName = filterName === '.show-all' ? '*' : filterName;
      $container.isotope({
          filter : filterName
      });
      return false;
  });


// Video Player Functionality

var tag = document.createElement('script');

tag.src = "https://www.youtube.com/iframe_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);


// This function creates an <iframe> (and YouTube player)
// after the API code downloads.
// $('#watch-event').click(function() {

var ytid = ($('#watch-event').data('ytid'));
console.log($('#watch-event').data('ytid'));

// Function creates an <iframe> (and YouTube player)
// on click and after the API code downloads.
var player;

function onPlayerReady(event) {
    event.target.playVideo();
}

$('#watch-event').click(function onYouTubeIframeAPIReady() {
    player = new YT.Player('player', {
      height: '350',
      width: '623',
      videoId: ytid,
      events: {
        'onReady': onPlayerReady,
        'onStateChange': function (event) {
              switch (event.data) {
                  case -1:
                      console.log ('unstarted');
                      break;
                  case 0:
                      $('#yt-theatre').animate({'padding-bottom': '34.5%'},{duration:700});
                      console.log ('ended');
                      break;
                  case 1:
                      $('#yt-theatre').animate({'padding-bottom': '42.5%'},{duration:700});
                      console.log ('playing');
                      break;
                  case 2:
                      $('#yt-theatre').animate({'padding-bottom': '34.5%'},{duration:700});
                      console.log ('paused');
                      break;
                  case 3:
                      $('#yt-theatre').animate({'padding-bottom': '34.5%'}, {duration:700});
                      console.log ('buffering');
                      break;
                  case 5:
                      console.log ('video cued');
                      break;
              }
          }
        }
    });
});

// Frontpage Slider – Owl Carousel
$( document ).ready(function() {
  $('.owl-carousel').owlCarousel({
    loop:true,
    nav:false,
    autoplay:true,
    autoHeight:true,
    autoplayTimeout:5000,
    transitionStyle : "fade",
    lazyLoad:true,
    autoplayHoverPause:true,
    responsiveClass:true,
    responsive:{
        0:{
            items:1
        },
        600:{
            items:1
        },
        1000:{
            items:1
        }
    }
  });

  var owl = $('.owl-carousel');
  owl.owlCarousel();
  $('.next').click(function() {
      owl.trigger('next.owl.carousel');
  })
  // Go to the previous item
  $('.prev').click(function() {
      // With optional speed parameter
      // Parameters has to be in square bracket '[]'
      owl.trigger('prev.owl.carousel', [1000]);
  });
});